mapper.select:
select count(member_email) 
from tb_account_member 
where member_code_yn='Y' and member_email = #{auth_id};

select member_tempcode 
from tb_account_member 
where member_code_yn='Y' and member_email = #{auth_id} and member_pass=#{auth_pass};

select (a.idx) as vod_idx, (select vod_path from tb_vod_repository where idx=a.vod_repo) as vod_path, (a.board_title) as vod_title, (a.board_content) as vod_content, ifnull(a.board_keyword,'') as vod_keyword, (select vod_play_time from tb_vod_repository where idx=a.vod_repo) as vod_play_time, (a.favorite_count) as favorite_count, (a.view_count) as view_count, (select main_thumbnail from tb_vod_repository where idx=a.vod_repo) as main_thumbnail, (a.category_idx) as board_cate_idx, (select category_name from tb_board_category where idx=a.category_idx) as board_cate_name from tb_board_repository a where 1=1 and a.del_flag='N'
and a.category_idx in
#{type}
order by a.reg_dt desc
order by a.view_count desc
limit 0,5;

select (a.idx) as vod_idx, (select vod_path from tb_vod_repository where idx=a.vod_repo) as vod_path, (a.board_title) as vod_title, (a.board_content) as vod_content, ifnull(a.board_keyword,'') as vod_keyword, (select vod_play_time from tb_vod_repository where idx=a.vod_repo) as vod_play_time, (a.favorite_count) as favorite_count, (a.view_count) as view_count, (select main_thumbnail from tb_vod_repository where idx=a.vod_repo) as main_thumbnail, (a.category_idx) as board_cate_idx, (select category_name from tb_board_category where idx=a.category_idx) as board_cate_name, date_format(reg_dt,'%Y.%m.%d') as vod_regdate from tb_board_repository a where 1=1 and a.del_flag='N'
and a.category_idx in
#{type}
order by a.reg_dt desc
order by a.view_count desc
limit #{start},#{end}
